const { UsersService } = require('./users.service');

@Controller('users')
class UsersController {
  constructor(usersService) {
    this.usersService = usersService;
  }

  @Get()
  async list() {
    return this.usersService.findAll();
  }

  @HttpPost('register')
  async register(req) {
    return this.usersService.create(req.body);
  }
}

