// src/posts/posts.service.js
const { Injectable, NotFoundException, ForbiddenException } = require('@nestjs/common');
const { Post } = require('./post.entity');
const { dataSource } = require('../db/data-source');

@Injectable()
class PostsService {
  constructor() {
    // –Ω–∞–ø—Ä—è–º—É—é –ø–æ–ª—É—á–∞–µ–º —Ä–µ–ø–æ–∑–∏—Ç–æ—Ä–∏–π –∏–∑ DataSource
    this.postRepository = dataSource.getRepository(Post);
  }

  async findAll() {
    return this.postRepository.find({ relations: ['author'] });
  }

  async findOne(id) {
    const post = await this.postRepository.findOne({ where: { id }, relations: ['author'] });
    if (!post) throw new NotFoundException();
    return post;
  }

  async create(data, user) {
    const post = this.postRepository.create({ ...data, author: user });
    return this.postRepository.save(post);
  }

  async update(id, data, user) {
    const post = await this.findOne(id);
    if (post.author.id !== user.sub && user.role !== 'admin') {
      throw new ForbiddenException();
    }
    Object.assign(post, data);
    return this.postRepository.save(post);
  }

  async remove(id, user) {
    const post = await this.findOne(id);
    if (post.author.id !== user.sub && user.role !== 'admin') {
      throw new ForbiddenException();
    }
    return this.postRepository.delete(id);
  }
}

module.exports = { PostsService };
