import { useEffect, useState } from "react";
import { useAuth } from "../contexts/AuthContext";
import { fetchProfile, updateUsername, updatePassword } from "../api";
import { Navigate } from "react-router-dom";

export default function ProfilePage() {
  const { token, logout } = useAuth();
  const [user, setUser] = useState(null);
  const [username, setUsername] = useState("");
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");

  useEffect(() => {
    if (!token) return;

    fetchProfile(token).then(data => {
      setUser(data);
      setUsername(data.username);
    });
  }, [token]);

  if (!token) {
    return <Navigate to="/login" replace />;
  }

  if (!user) return <p>Loading...</p>;

  const saveUsername = async () => {
    const updated = await updateUsername(token, username);
    localStorage.setItem("user", JSON.stringify(updated));
    alert("Username updated");
  };

  const savePassword = async () => {
    await updatePassword(token, {
      oldPassword,
      newPassword,
    });
    alert("Password updated");
    setOldPassword("");
    setNewPassword("");
  };

  return (
    <div className="max-w-md mx-auto p-6 space-y-6">
      <h1 className="text-2xl font-bold">Profile</h1>

      <div>
        <label>Username</label>
        <input
          className="border p-2 w-full"
          value={username}
          onChange={e => setUsername(e.target.value)}
        />
        <button
          onClick={saveUsername}
          className="mt-2 border px-4 py-1"
        >
          Save username
        </button>
      </div>

      <div>
        <label>Old password</label>
        <input
          type="password"
          className="border p-2 w-full"
          value={oldPassword}
          onChange={e => setOldPassword(e.target.value)}
        />

        <label className="mt-2 block">New password</label>
        <input
          type="password"
          className="border p-2 w-full"
          value={newPassword}
          onChange={e => setNewPassword(e.target.value)}
        />

        <button
          onClick={savePassword}
          className="mt-2 border px-4 py-1"
        >
          Change password
        </button>
      </div>

      <button
        onClick={logout}
        className="text-red-600 underline"
      >
        Logout
      </button>
    </div>
  );
}
