import { useEffect, useState } from "react";
import { Navigate, useNavigate, Link } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";
import { updateUsername } from "../api";

export default function ProfilePage() {
  const { token, user, loading, refreshMe, logout } = useAuth();
  const [username, setUsername] = useState(user?.username || "");
  const [saving, setSaving] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    if (!token) return;
    refreshMe(); // подтянуть актуального юзера
    // eslint-disable-next-line
  }, [token]);

  useEffect(() => {
    setUsername(user?.username || "");
  }, [user]);

  if (loading) return null;
  if (!token) return <Navigate to="/login" replace />;

  const submit = async (e) => {
    e.preventDefault();
    setSaving(true);
    try {
      await updateUsername(token, username);
      await refreshMe();
      navigate("/posts");
    } finally {
      setSaving(false);
    }
  };

  return (
    <div className="max-w-xl mx-auto p-6">
      <div className="flex items-center justify-between mb-6">
        <h1 className="text-2xl font-bold">Profile</h1>
        <button
          onClick={logout}
          className="text-red-600 hover:underline"
        >
          Logout
        </button>
      </div>

      <div className="border rounded p-4 mb-6">
        <p className="text-gray-600 text-sm">Your ID</p>
        <p className="font-mono break-all">{user?.id}</p>
      </div>

      <form onSubmit={submit} className="space-y-4 border rounded p-4">
        <div>
          <label className="block text-sm text-gray-700 mb-1">Username</label>
          <input
            className="w-full border rounded px-3 py-2"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            minLength={3}
            required
          />
        </div>

        <button
          disabled={saving}
          className="w-full border rounded px-3 py-2 bg-black text-white hover:opacity-90 disabled:opacity-50"
        >
          {saving ? "Saving..." : "Save username"}
        </button>
      </form>

      <div className="mt-6">
        <Link to="/posts" className="text-blue-600 hover:underline">
          ← Back to posts
        </Link>
      </div>
    </div>
  );
}
