import { createContext, useContext, useState, useEffect } from "react";
import { loginUser, logoutUser, fetchMe } from "../api";

const AuthContext = createContext(null);

export const useAuth = () => {
  const ctx = useContext(AuthContext);
  if (!ctx) throw new Error("useAuth must be used within an AuthProvider");
  return ctx;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(null);
  const [loading, setLoading] = useState(true);

  // ✅ восстановление с localStorage
  useEffect(() => {
    const storedToken = localStorage.getItem("token");
    const storedUser = localStorage.getItem("user");

    if (storedToken) setToken(storedToken);
    if (storedUser) setUser(JSON.parse(storedUser));

    setLoading(false);
  }, []);

  const login = async (credentials) => {
    const response = await loginUser(credentials);
    const t = response.access_token;

    setToken(t);
    localStorage.setItem("token", t);

    // ✅ получаем актуального юзера с бэка
    const me = await fetchMe(t);
    setUser(me);
    localStorage.setItem("user", JSON.stringify(me));

    return response;
  };

  const refreshMe = async () => {
    if (!token) return;
    const me = await fetchMe(token);
    setUser(me);
    localStorage.setItem("user", JSON.stringify(me));
    return me;
  };

  const logout = async () => {
    try {
      if (token) await logoutUser(token);
    } finally {
      setToken(null);
      setUser(null);
      localStorage.removeItem("token");
      localStorage.removeItem("user");
    }
  };

  return (
    <AuthContext.Provider value={{ user, token, login, logout, loading, refreshMe }}>
      {children}
    </AuthContext.Provider>
  );
};
